import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";


const baseUrl='http://127.0.0.1:8000/api';

function UserFavoriteCourses(){
    useEffect(()=>{
        document.title='User Fav Courses';
    });

    const [courseData, setCourseData] = useState([]);

    const userId=localStorage.getItem('userId');

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch-fav-courses/'+ userId).then((res)=>{
          setCourseData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);
    return(
        <>
        <UserHeader />
       
        <div className="container mt-4" >
        <div id="my"><h4>Менің сүйікті курстарым</h4></div>    
        <div className="row mb-4">
         {courseData.map((row, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
           
            <div className="card-body">
             <Link to={'/course_detail/'+ row.course.id}><h5 className="card-title">{row.course.title}</h5></Link>
             <p>{row.course.teacher.full_name}</p>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>
        </>
    );
}
export default UserFavoriteCourses;
