import {Link} from 'react-router-dom';
import {useEffect, useState} from 'react';
import axios from 'axios';

const baseUrl='http://127.0.0.1:8000/api/teacher/';
function UserSignup() {

  useEffect(()=>{
    document.title='User Sign UP';
  });

  const [userData, setUserData] = useState({
    'full_name': '',
    'email': '',
    'password': '',
    'username': '',
    'interest': '',
    'status': ''
  });

  //Change Element Value
  const handleChange=(event)=>{
    setUserData
    ({
      ...userData,
      [event.target.name]: event.target.value
    });
  }
  //End

  //Submit Form
  const submitForm=()=>{
    const teacherFormData = new FormData();
    teacherFormData.append("full_name", userData.full_name)
    teacherFormData.append("email", userData.email)
    teacherFormData.append("password", userData.password)
    teacherFormData.append("username", userData.username)
    teacherFormData.append("mobile_no", userData.mobile_no)
    teacherFormData.append("interest", userData.interest)

    try{
      axios.post(baseUrl, teacherFormData).then((response)=>{
        setUserData
        ({
          'full_name': '',
          'email': '',
          'password': '',
          'username': '',
          'mobile_no': '',
          'interest': '',
          'status': 'success'
        });
      });
    }catch(error){
        console.log(error);
        setUserData
        ({'status': 'error'});
    }

    

  };
  //End

  //Redirecting to the Profile
  const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
  if(teacherLoginStatus==='true'){
    window.location.href='/teacher-profile';
  }

  return (
    <> 
        {/* Left Part */}
       <div className="leftSide"></div>
        
        {/* Right Part */}
       <div className="rightSide">
         {/*userData.status ==='success' && <p className="text-success">Сәтті тіркелдіңіз!</p>*/}
         {/*userData.status ==='error' && <p className="text-danger">Тіркелу мүмкін емес, тағы да байқап көріңіз!</p>*/}
         <Link to="/"><p className="logo" >lulu school</p></Link>
         <div id = "register-info">
         <h3>Аккаунтқа тіркелу</h3>

           <div className="form-group">
             <input name="full_name" value={userData.full_name} onChange={handleChange} type="text" className="form-control" id="InputUsername1" aria-describedby="emailHelp" placeholder="Аты - жөні"></input>
           </div>

           <div className="form-group">
             <input name="email" value={userData.email} onChange={handleChange} type="email" className="form-control" id="InputEmail1" aria-describedby="emailHelp" placeholder="Электрондық пошта"></input>
           </div>

           <div className="form-group">
             <input name="username" value={userData.username} onChange={handleChange} type="text" className="form-control" id="Inputusername1" aria-describedby="emailHelp" placeholder="Біліктілік"></input>
           </div>

           <div className="form-group">
             <input name="password" value={userData.password} onChange={handleChange} type="password" className="form-control" id="InputPassword1" placeholder="Құпия сөз"></input>
           </div>

           <div className="form-group">
             <textarea name="interest" value={userData.interest} onChange={handleChange} type="text" className="form-control" id="Inputinterest" aria-describedby="emailHelp" placeholder="Қабілеттілік пен дағдылар"></textarea>
           </div>
           
           <div className="form-group">
             <button onClick={submitForm} type="submit">Тіркелу</button>
           </div>

         <p id="bottom-words">Тіркеліп қойдыңыз ба? <Link to="/user-login" id="a">Кіру</Link></p>
         </div>
         </div>

    </>
  );
}

export default UserSignup;
